
public class WhileExample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1, ep=5;  //initialization
		
		while(i<=ep)  //condition
		{
			
			System.out.print(i+" ");
			i++;     //iteration
		}
		//i++;
		System.out.println("Done");
	}

}
